exports.newCartHashToken = () => {
    const chars = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
	var result = '';
	for (var i = 56; i > 0; --i) result += chars[Math.floor(Math.random() * chars.length)];
	return result;
}


exports.getBasePath = ({ store, theme }) => {
    var base_path = process.env.THEME_DIR || `${process.env.NODE_ENV === 'development' ? '../themes-dev' : '../themes'}/${store.slug}/${theme.slug}/${theme.version}/`;
    return `${base_path}`;
}


exports.getAssetURL = ({ base_url, theme, store }) => {
    var asset_path = process.env.THEME_DIR ? "" : `${store.slug}/${theme.slug}/${theme.version}/`;
    return `${base_url}/${asset_path}assets`;
}